import {
  PROJECT_TASKS,
  ADD_NEW_TASK,
  VALIDATE_FORM_TASK,
  DELETE_TASK,
  TASK_STATE,
} from '../../types';

export default (state, action) => {
  switch (action.type) {
    case PROJECT_TASKS:
      return {
        ...state,
        projectTask: state.tasks.filter(
          (task) => task.projectId === action.payload,
        ),
      };
    case ADD_NEW_TASK:
      return {
        ...state,
        tasks: [action.payload, ...state.tasks],
        error: false,
      };
    case VALIDATE_FORM_TASK:
      return { ...state, error: true };
    case DELETE_TASK:
      return {
        ...state,
        tasks: state.tasks.filter((task) => task.id !== action.payload),
      };
    case TASK_STATE:
      return {
        ...state,
        tasks: state.tasks.map((task) => task.id === action.payload.id),
      };
    default:
      return state;
  }
};
