import React, { useReducer } from 'react';
import taskContext from './taskContex';
import taskReducer from './taskReducer';

import {
  PROJECT_TASKS,
  ADD_NEW_TASK,
  VALIDATE_FORM_TASK,
  DELETE_TASK,
  TASK_STATE,
} from '../../types';

const TaskState = (props) => {
  const initialSatate = {
    tasks: [
      {
        id: 1,
        name: 'Example',
        state: true,
        projectId: 1,
      },
      {
        id: 2,
        name: 'Example 2',
        state: false,
        projectId: 2,
      },
    ],
    projectTask: null,
    error: false,
  };

  const [state, dispatch] = useReducer(taskReducer, initialSatate);

  //Get task by a single projectId

  const getTask = (projectId) => {
    dispatch({
      type: PROJECT_TASKS,
      payload: projectId,
    });
  };
  const addTask = (task) => {
    dispatch({
      type: ADD_NEW_TASK,
      payload: task,
    });
  };

  const onError = () => {
    dispatch({ type: VALIDATE_FORM_TASK });
  };

  const deleteTask = (id) => {
    dispatch({ type: DELETE_TASK, payload: id });
  };

  const changeState = (task) => {
    dispatch({ type: TASK_STATE });
  };

  return (
    <taskContext.Provider
      value={{
        tasks: state.tasks,
        projectTask: state.projectTask,
        error: state.error,
        getTask,
        addTask,
        onError,
        deleteTask,
      }}>
      {props.children}
    </taskContext.Provider>
  );
};

export default TaskState;
