import {
  NEW_PROJECT,
  GET_PROJECTS,
  ADD_PEOJECT,
  VALIDATE_FORM,
} from '../../types';

export default (state, action) => {
  switch (action.type) {
    case NEW_PROJECT:
      return { ...state, newProject: true };
    case GET_PROJECTS:
      return { ...state, projects: action.payload };
    case ADD_PEOJECT:
      return {
        ...state,
        projects: [...state.projects, action.payload],
        newProject: false,
      };
    case VALIDATE_FORM:
      return { ...state, error: true };
    default:
      return state;
  }
};
