import React, { useReducer } from 'react';
import taskContext from './taskContex';
import taskReducer from './taskReducer';
import axiosClient from '../../config/axios';
import {
  PROJECT_TASKS,
  ADD_NEW_TASK,
  VALIDATE_FORM_TASK,
  DELETE_TASK,
  TASK_STATE,
  CURREENT_TASK,
  UPDATE_TASK,
  CLEAN,
} from '../../types';

const TaskState = (props) => {
  const initialSatate = {
    projectTask: [],
    error: false,
    selectedTask: null,
  };

  const [state, dispatch] = useReducer(taskReducer, initialSatate);

  //Get task by a single projectId

  const getTask = (projectId) => {
    try {
      dispatch({
        type: PROJECT_TASKS,
        payload: projectId,
      });
    } catch (error) {
      console.log(error);
    }
  };
  const addTask = async (task) => {
    try {
      const response = await axiosClient.post('/api/tasks', task);
      // console.log(response.data.task);
      dispatch({
        type: ADD_NEW_TASK,
        payload: response.data.task,
      });
    } catch (error) {
      console.log(error);
    }
  };

  const onError = () => {
    dispatch({ type: VALIDATE_FORM_TASK });
  };

  const deleteTask = (id) => {
    dispatch({ type: DELETE_TASK, payload: id });
  };

  const changeState = (task) => {
    dispatch({ type: TASK_STATE, payload: task });
  };

  const selectTask = (task) => {
    dispatch({ type: CURREENT_TASK, payload: task });
  };

  const updateTask = (task) => {
    dispatch({ type: UPDATE_TASK, payload: task });
  };

  const cleanTask = () => {
    dispatch({ type: CLEAN });
  };

  return (
    <taskContext.Provider
      value={{
        projectTask: state.projectTask,
        error: state.error,
        selectedTask: state.selectedTask,
        getTask,
        addTask,
        onError,
        deleteTask,
        changeState,
        selectTask,
        updateTask,
        cleanTask,
      }}>
      {props.children}
    </taskContext.Provider>
  );
};

export default TaskState;
