import React, { useReducer } from 'react';
import taskContext from './taskContex';
import taskReducer from './taskReducer';
import { v4 as uuidv4 } from 'uuid';

import {
  PROJECT_TASKS,
  ADD_NEW_TASK,
  VALIDATE_FORM_TASK,
  DELETE_TASK,
  TASK_STATE,
  CURREENT_TASK,
  UPDATE_TASK,
  CLEAN,
} from '../../types';

const TaskState = (props) => {
  const initialSatate = {
    tasks: [
      {
        id: 1,
        name: 'Example',
        state: true,
        projectId: 1,
      },
      {
        id: 2,
        name: 'Example 2',
        state: false,
        projectId: 2,
      },
    ],
    projectTask: null,
    error: false,
    selectedTask: null,
  };

  const [state, dispatch] = useReducer(taskReducer, initialSatate);

  //Get task by a single projectId

  const getTask = (projectId) => {
    dispatch({
      type: PROJECT_TASKS,
      payload: projectId,
    });
  };
  const addTask = (task) => {
    task.id = uuidv4();
    dispatch({
      type: ADD_NEW_TASK,
      payload: task,
    });
  };

  const onError = () => {
    dispatch({ type: VALIDATE_FORM_TASK });
  };

  const deleteTask = (id) => {
    dispatch({ type: DELETE_TASK, payload: id });
  };

  const changeState = (task) => {
    dispatch({ type: TASK_STATE, payload: task });
  };

  const selectTask = (task) => {
    dispatch({ type: CURREENT_TASK, payload: task });
  };

  const updateTask = (task) => {
    dispatch({ type: UPDATE_TASK, payload: task });
  };

  const cleanTask = () => {
    dispatch({ type: CLEAN });
  };

  return (
    <taskContext.Provider
      value={{
        tasks: state.tasks,
        projectTask: state.projectTask,
        error: state.error,
        selectedTask: state.selectedTask,
        getTask,
        addTask,
        onError,
        deleteTask,
        changeState,
        selectTask,
        updateTask,
        cleanTask,
      }}>
      {props.children}
    </taskContext.Provider>
  );
};

export default TaskState;
