import React, { useReducer } from 'react';
import { v4 as uuidv4 } from 'uuid';
import projectContext from './projectContext';
import projectReducer from './projectReducer';
import {
  NEW_PROJECT,
  GET_PROJECTS,
  ADD_PEOJECT,
  VALIDATE_FORM,
  CURRENT_PROJECT,
  DELETE_PROJECT,
} from '../../types';

const ProjectState = (props) => {
  const projects = [
    { id: 1, name: 'Tienda Virtual' },
    { id: 2, name: 'Tienda Virtual' },
    { id: 3, name: 'Tienda Virtual' },
  ];
  const initialSatate = {
    newProject: false,
    projects: [],
    error: false,
    project: null,
  };

  const [state, dispatch] = useReducer(projectReducer, initialSatate);

  const showForm = () => {
    dispatch({ type: NEW_PROJECT });
  };

  const getProjects = () => {
    dispatch({
      type: GET_PROJECTS,
      payload: projects,
    });
  };

  const addProject = (project) => {
    project.id = uuidv4();
    dispatch({
      type: ADD_PEOJECT,
      payload: project,
    });
  };

  const onError = () => {
    dispatch({
      type: VALIDATE_FORM,
    });
  };

  const selectProject = (projectId) => {
    dispatch({
      type: CURRENT_PROJECT,
      payload: projectId,
    });
  };

  const deleteProject = (projectId) => {
    dispatch({
      type: DELETE_PROJECT,
      payload: projectId,
    });
  };
  return (
    <projectContext.Provider
      value={{
        newProject: state.newProject,
        projects: state.projects,
        error: state.error,
        project: state.project,
        showForm,
        getProjects,
        addProject,
        onError,
        selectProject,
        deleteProject,
      }}>
      {props.children}
    </projectContext.Provider>
  );
};

export default ProjectState;
