import { PROJECT_TASKS, ADD_NEW_TASK } from '../../types';

export default (state, action) => {
  switch (action.type) {
    case PROJECT_TASKS:
      return {
        ...state,
        projectTask: state.tasks.filter(
          (task) => task.projectId === action.payload,
        ),
      };
    case ADD_NEW_TASK:
      return { ...state, tasks: [...state.tasks, action.payload] };
    default:
      return state;
  }
};
